import React, { Component } from 'react';
import ReactDOM from 'react-dom';
import './App.css';

//From: https://codepen.io/zebapy/pen/LGjyWJ

export default class App extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      quotes: {},
      quote: '',
      author: '',

      count: 0
    };
  }

  componentDidMount() {
    fetch("https://raw.githubusercontent.com/DeusExVagina/sexscenegenerator/master/sexscenegenerator/JSONFIles/kissingactions.json")
      .then(response => response.json())
      .then(data => {
        this.setState({
          kissingActions: data.lines
        });
      });
  }
  componentDidUpdate() {
  }

  _randomAll() {
    this.setState({
      quote: ""
    })
    var kissingscene = '';    
    var femaleorgasmScene = getFemaleOrgasmScene(this.state.orgasmActions);
    this.setState({
      sexaction: this.state.sexaction + kissingscene + undressingscene + penetrationstartScene + penetrationScene + femaleorgasmScene
    })
  };

  handleGetNameClick = (e) => {

  };

  render() {
    const { sexaction, lastNamePrefix, lastNameSuffix } = this.state;

    return (
      <div className="container">
        <div className="name">
          <div>
            <span className="firstname">Stoic Quote Generator</span>
            <br />
          </div>

        </div>
        <p>{spinText(genderReplace(sexaction))}</p>
        <div className="m-y">
          <button className="btn btn-random" onClick={this._randomAll.bind(this)}>Generate random stoic quote</button>
        </div>
        <small className="randomize-note">Like this thing?</small>
        <Button name="firstName" onClick="https://keypressingmonkey.com">My Website</Button>
        <Button name="lastNamePrefix" onClick="https://www.youtube.com/channel/UCMdFN7FUC4HilpDBEw0uvJw">My Youtube</Button>
      </div>
    )
  };
}

function getRandom(items) {
  return items[Math.floor(Math.random() * items.length)];
}
function spinText(inputString) {
  //copied from: https://ctrlq.org/code/20277-javascript-spintax

  var matches, options, random;

  var regEx = new RegExp(/{([^{}]+?)}/);

  while ((matches = regEx.exec(inputString)) !== null) {
    options = matches[1].split("|");
    random = Math.floor(Math.random() * options.length);
    inputString = inputString.replace(matches[0], options[random]);
  }
  return inputString;
}

function getRandomInt(max) {
  return Math.floor(Math.random() * Math.floor(max));
}

const Button = ({ name, onClick, children }) => {
  return (
    <form method="get" action={onClick} target="_blank">
      <input className="btn btn-randomize-namepart" type="submit" name={name} value={children} /></form>
  )
}

function getQuotes(quotes) {
  var quote = '';
  quote = getRandom(quotes.filter(function (item) {
    return item.genderdirection.startsWith("m")//todo wrong gender, write some female orgasms
  }));
  return quote;

}
