import React from "react";
import ReactDOM from "react-dom";
import Haikunator from "haikunator";

const haikunator = new Haikunator({
  seed: "custom-seed",
  defaults: {
    tokenLength: 0
  }
});

class NameGen extends React.Component {
  state = {
    name: null
  };
  gen = () => {
    this.setState({
      name: haikunator.haikunate({ tokenLength: 0, delimiter: "-" })
    });
  };
  componentDidMount() {
    this.gen();
  }
  render() {
    return (
      <React.Fragment>
        <div>
          <button style={{ fontSize: "2em" }} onClick={this.gen}>
            Generate
          </button>
        </div>
        <br />
        <br />
        <div style={{ fontSize: "3em" }}>{this.state.name}</div>
      </React.Fragment>
    );
  }
}
function App() {
  return (
    <div
      className="App"
      style={{ textAlign: "center", fontFamily: "sans-serif" }}
    >
      <h1>release name generator</h1>
      <br />
      <br />
      <NameGen />
    </div>
  );
}

const rootElement = document.getElementById("root");
ReactDOM.render(<App />, rootElement);
